package proyectounidad1.ejemplosunidad1;

public class JavaOperadoresAsignacion {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int num1 = 8, num2 = 12;
		int res = 0;

		System.out.println(res);

		res = num1 + num2;

		System.out.println(res);
		res = res + num1;
		System.out.println(res);

		res += num1;
		System.out.println(res);

		res -= num1;
		System.out.println(res);

		res *= num1;
		System.out.println(res);

		res /= num1;
		System.out.println(res);

	}

}
